using System;
using System.Net.Http;
using System.Threading.Tasks;
using Facebook;

public class Script : BaseRequest
{
    string baseUri = "https://graph.facebook.com/v5.0/" +
                "act_<ID>" +
                "/reachestimate" +
                "?access_token=<TOKEN>";

    public override void GetSeries()
    {
        int[] min_ages = new int[] { 13, 24, 35, 45 };
        int[] max_ages = new int[] { 24, 35, 45, 65 };

        for (int i = 0; i < 4; i++)
        {
            var targeting_spec = new
            {
                geo_locations = new
                {
                    countries = new string[] { "US" }
                },
                age_min = min_ages[i],
                age_max = max_ages[i]
            };

            HttpRequestMessage msg = new HttpRequestMessage();
            msg.Method = HttpMethod.Get;
            msg.RequestUri = new Uri(baseUri + "&targeting_spec=" + ToJson(targeting_spec));

            var serie = RequestSerieValue(msg, "$.data.users");

            AddSerie(serie, "Usuários entre " + min_ages[i] + " e " + max_ages[i]);
        }
    }
}
